mapper.select:
 select
  job_id, bean_name, method_name, method_params, cron_expression, job_status, remark, create_time, update_time
from test.sys_job where job_status=#{status,jdbcType=INTEGER}

select
  job_id, bean_name, method_name, method_params, cron_expression, job_status, remark, create_time, update_time
from test.sys_job


        and job_id = #{jobId}


        and bean_name = #{beanName}


        and method_name = #{methodName}


        and method_params = #{methodParams}


        and cron_expression = #{cronExpression}


        and job_status = #{jobStatus}


        and remark = #{remark}


        and create_time = #{createTime}


        and update_time = #{updateTime}
